package com.capgemini.day2;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;
		int num2=20;
		
		int num3=num1+num2;
		System.out.println("The sum of two number:" + num3);
		
		int num4=num3-num1;
		System.out.println("The sum of two number:" + num4);
		
		int num5=num4*num2;
		System.out.println("The sum of two number:" + num5);
		
		int num6=num2/num1;
		System.out.println("The sum of two number:" + num6);
		
		int num7=num4%num1;
		System.out.println("The sum of two number:" + num7);
		
		int num8=num1;
		System.out.println("The sum of two number:" + num8);
		
	}

}
